import {Component, Input, OnInit} from '@angular/core';
import {Card} from '../../../../model/card/card.model';

@Component({
  selector: 'app-card-summary',
  templateUrl: './summary.component.html',
  styleUrls: ['./summary.component.css']
})
export class SummaryComponent implements OnInit {

   @Input() card: Card;
  @Input() listIndex: number;
  @Input() cardIndex: number;


  constructor() { }

  ngOnInit() {

  }

  identifyCardBeingDragged(dragEvent: DragEvent) {

    dragEvent.dataTransfer.effectAllowed = 'move'
    dragEvent.dataTransfer.dropEffect= 'move'
    const transferObject = {
      'listIndex' : this.listIndex,
      'cardIndex' : this.cardIndex
    };
    dragEvent.dataTransfer.setData( 'text', JSON.stringify(transferObject));
  }

  allowCardDragToBeDropped(dragEvent: DragEvent) {
    dragEvent.dataTransfer.dropEffect= 'move'
    dragEvent.preventDefault();
  }
}
<div class="mb-3" [draggable]="'true'" (dragstart)="identifyCardBeingDragged($event)"
  id="list-{{listIndex}}-card-{{cardIndex}}" attr.listIndex="{{listIndex}}" attr.cardIndex="{{cardIndex}}"
  (dragover)="allowCardDragToBeDropped($event)">
  <div class="p-2 border-round-sm bg-white">
    <i class="pi pi-times"></i>
    <i class="pi pi-check" style="font-size: 2rem"></i>

    <h4 class="no-wrap center m-0 text-orange-600" [(appContentEdit)]="card.header"> </h4>
    <p class="no-wrap" [(appContentEdit)]="card.summary"></p>
  </div>
</div>
