import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'searchFilter'
})
export class SearchFilterPipe implements PipeTransform {

  transform(list: any, ...args: any[]) {
    let tempArr = []
    if(args[0] !== ''){
       tempArr = list.map(l=> l.cards = l.cards.filter(c=> c.header.indexOf(args[0]) !== -1|| c.summary.indexOf(args[0]) !== -1));
    }
    else {
      return list
    }
  }

}
