import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'searchFilter'
})
export class SearchFilterPipe implements PipeTransform {

  transform(list: any, ...args: any[]) {
    console.log(list)
    console.log(args)
    list.map(l=> {
      l.cards = l.cards.filter(c=> {
        if(c.headers.indexOf(args[0]) !== -1|| c.headers.indexOf(args[0]) !== -1)
      })
    })
    return list;
  }

}
